        d="M69.499,164.6C70.499,165.975,69.249,167.975,68.499,169.1C67.856,170.065,67.404,171.125,69.323,173.376C74.606,173.573,82.22999999999999,173.63400000000001,86.306,172.625C85.868,171.935,85.547,171.048,85.547,169.822C85.547,166.572,84.672,166.697,83.922,166.072S80.172,164.822,81.797,161.822C82.965,159.665,83.74199999999999,159.123,83.857,158.571C82.663,158.581,81.192,158.625,80.736,158.625C79.971,158.625,75.87700000000001,159.5,75.87700000000001,159.5L75.251,157.75L69.90700000000001,158.379C70.224,159.625,70.48,160.475,70.48,160.475S68.499,163.225,69.499,164.6Z"
        title="Naypyitaw"
        id="MM-18"
        :cases="this.npt.confirmed"
        :deaths="this.npt.deaths"
        :recovered="this.npt.recovered"
        :activeCases="this.npt.active"
        :fill="this.npt.colorCode"
      />
    </svg>
  </div>
</template>
<script>
export default {
  data() {
    return {
      sg: "",
      bg: "",
      mg: "",
      mdl: "",
      tnt: "",
      ygn: "",
      aya: "",
      kc: "",
      ky: "",
      kyin: "",
      chin: "",
      mon: "",
      rk: "",
      shan: "",
      npt: "",
      fullscreenLoading: false
    };
  },
  mounted() {
    this.fetchMapCases();
  },
  methods: {
    async fetchMapCases() {
      this.axios
        .get(`https://thantthet.github.io/covid19-api/data.json`)
        .then(response => {
          var states = response.data.statewise;
          for (var i in states) {
            var confirmed = states[i].confirmed;
            var colorTag = [
              {
                colorCode: ""
              }
            ];
            if (confirmed > 0 && confirmed <= 10) {
              colorTag.colorCode = "#f5c8b4";
            } else if (confirmed > 10 && confirmed <= 30) {
              colorTag.colorCode = "#eb7a5d";
            } else if (confirmed > 30 && confirmed <= 100) {
              colorTag.colorCode = "#da4c3b";
            } else if (confirmed > 100) {
              colorTag.colorCode = "#ad2b26";
            }
            states[i].colorCode = colorTag.colorCode;
          }
          this.sg = states[1];
          this.bg = states[2];
          this.mg = states[3];
          this.mdl = states[4];
          this.tnt = states[5];
          this.ygn = states[6];
          this.aya = states[7];
          this.kc = states[8];
          this.ky = states[9];
          this.kyin = states[10];
          this.chin = states[11];
          this.mon = states[12];
          this.rk = states[13];
          this.shan = states[14];
          this.npt = states[15];
        });
    }
  }
};
</script>
